function showPasswordToggle(){[].slice.call(document.querySelectorAll(".toggle-password")).map(function(group){const passwordInput=group.querySelector(":scope > [type='password']");const togglePasswordButton=group.querySelector(":scope button.d-none");togglePasswordButton.classList.remove("d-none");togglePasswordButton.innerHTML='<i class="bi bi-eye-slash"></i>';togglePasswordButton.addEventListener("mouseover",function(){passwordInput.type="text";togglePasswordButton.innerHTML='<i class="bi bi-eye"></i>'});togglePasswordButton.addEventListener("mouseout",function(){passwordInput.type="password";togglePasswordButton.innerHTML='<i class="bi bi-eye-slash"></i>'});})}
